mapper.select:
select contest_id,contest.teacher,startTime,endTime,contest.paper_id,contestpaper.title as papertitle,contest.title,user.realName from contest,user,contestpaper
 
   
     and contest.fk_course_id = #{fkCourseId}
   
   
    and user.user_id = #{userId}
  
   and contest.teacher = user.user_id
   and contest.paper_id = contestpaper.paper_id
 
order by endTime desc

select contest_id,contest.teacher,startTime,endTime,contest.paper_id,contestpaper.title as papertitle,contest.title,user.realName from contest,user,contestpaper
and contest.fk_course_id = #{fkCourseId}
and user.user_id = #{userId}
and contest.teacher = user.user_id and contest.paper_id = contestpaper.paper_id and (contest_id=#{0} or contest.title=#{0} or contestpaper.title=#{0} or user.realName=#{0})
order by endTime desc

select contest_id,contest.teacher,startTime,endTime,contest.paper_id,contestpaper.title as papertitle,contest.title,user.realName from contest,user,contestpaper
and contest.fk_course_id = #{fkCourseId}
and user.user_id = #{userId}
and contest.teacher = user.user_id and contest.paper_id = contestpaper.paper_id and (startTime between #{1} and #{2})
order by endTime desc

select contest_id,contest.teacher,startTime,endTime,contest.paper_id,contestpaper.title as papertitle,contest.title,user.realName from contest,user,contestpaper
and contest.fk_course_id = #{fkCourseId}
and user.user_id = #{userId}
and contest.teacher = user.user_id and contest.paper_id = contestpaper.paper_id and (contest_id=#{keyword} or contest.title=#{keyword} or contestpaper.title=#{keyword} or user.realName=#{keyword}) and (startTime between #{startTime} and #{endTime})
order by endTime desc
